import database from '../database/mysql';
import Bluebird from 'bluebird';
import jwt from 'jsonwebtoken';
import bcrypt from 'bcrypt';
import bodyParser from 'body-parser';

const { con } = database;

Bluebird.promisifyAll(jwt);
Bluebird.promisifyAll(bcrypt);

const createUser = async (req, res, next) => {
    const {
      name,
      username, 
      email, 
      phonenumber,
      password
    }: {
      name: String,
      username: String,
      email: String,
      phonenumber: String,
      password: String
    } = req.body;

    console.log(req.body)
  
    const salt = bcrypt.genSaltSync(10)
    const getRounds = bcrypt.getRounds(salt); 
    const passHash = bcrypt.hashSync(password, getRounds);
  
    const createAt = new Date(Date.now());
    
    try {
      const createNewUser = 'INSERT INTO users (name, username, email, phonenumber, password, salt, created_at) VALUES (?,?,?,?,?,?,?)';
      con.query(createNewUser, [name, username, email, phonenumber, passHash, salt, createAt], (err, results) => {
        if (err) {
          console.error(err);
        }
        console.log(results);
      });
      res.status(201).send({ success: true, message: 'New user was successfully created', data: {name, username, email, phonenumber, password} });
    } catch (error) {
      res.status(500).send({ success: false, message: 'Server error' });
    }
    
    await next;
  }

  const login = async(req, res, next) => {
    const { email, password }: { email: string, password: string } = req.body;

    const userWithEmail = 'SELECT * FROM users WHERE email = ?';
    return con.query(userWithEmail, email, (err, results) => {
      if (err) {
        console.error(err);
      }
        const user = results.find(emailObj => emailObj.email === email);
      if (results && results.length && user.email) {
        const matchPassword: boolean = bcrypt.compareSync(password, user.password);
        if (matchPassword) {
          delete user.password;
          delete user.salt;
          const userId = user.id;
          const token = jwt.sign({ user }, 'aaaa', { expiresIn: '1h'});

          res.status(200).send({message: 'Logged in', token: token});
        } else {
          res.status(403).send('Password is not correct');
        }
      } else {
        res.status(404).send(`User with email ${email} not found!`);
      }
    });
    await next;
  }


      const ShowUserProfile = async(req, res, next) => {
        const { id }: { id: String } = req.params;
        
        const listUsers = 'SELECT * FROM users';
        return con.query(listUsers, (err, results) => {
          if (err) {
            console.error(err);
          }
          const users = results;
          const usersIds = users.map(user => user.id);
      
          if (usersIds.includes(Number(id))) {
            const SelectUsersById = 'SELECT name, username, email, phonenumber, password, credit_option, country, creditcard FROM users WHERE id = ?';
             return con.query(SelectUsersById, [Number(id)], (err, results, fields) => {
              if (err) {
                console.error(err);
              }
              res.status(200).send(results);
            });
          } else {
            res.status(404).send(`User with id ${id} does not exist. Please try again`);
          }
        });
      
        await next;
      }
    
      const UpdateUserDetails = async(req, res, next) => {
        const { id }: { id: string } = req.params;
        const {
          name,
          username,
          email,
          phonenumber,
          password,
        }: {
          name: ?String,
          username: String,
          email: String,
          phonenumber: String,
          password: ?String,
        } = Object.assign({}, req.body);
      
        const userId = req.body.id;
        if (userId) {
          res.status(403).send(`Id ${id} should not be overwritten`);
        } else {
          if (password && password.length) {
            const salt = bcrypt.genSaltSync(10);
            const getRounds = bcrypt.getRounds(salt);
            const passHash = bcrypt.hashSync(password, getRounds);
            
            const updateUserDetails = 'UPDATE users SET name = ?, username = ?, email = ?, phonenumber = ?, password = ? WHERE id = ?';
            return con.query(updateUserDetails, [name, username, email, phonenumber, passHash, Number(id)], (err, results) => {
              if (err) {
                console.error(err);
              }
              res.status(204).send(results);
              console.log(results);
            });
          
          } else {
            res.status(404).send('You must have a password');
          }
        }
        await next;
      }
    
      const deleteUser = async(req, res, next) => {
        const { id }: { id: string } = req.params;
        const deleteUserById = 'DELETE FROM users WHERE id = ?';
        return con.query(deleteUserById, parseInt(id), (err, results) => {
          if (err) {
            res.status(500).send(`There was a problem deleting the user.`);
          }
          res.status(202).send(`User with id ${id} is removed.`);
        });
      
        await next;
      }
 
  const createCreditCard = async (req, res, next) => {
    const {
      name,
      type, 
      number, 
      expiration,
      svss
    }: {
      name: String,
      type: String,
      number: String,
      expiration: String,
      svss: String
    } = req.body;

    const createAt = new Date(Date.now());

    try {
      const createNewCreditCard = 'INSERT INTO creditcards (name, type, number, expiration, svss) VALUES (?,?,?,?,?)';
      con.query(createNewCreditCard, [name, type, number, expiration, svss], (err, results) => {
        if (err) {
          console.error(err);
        } else {
          console.log(results);
          const JoinCreditCard = 'UPDATE users SET creditcard=? WHERE name=?';
          con.query(JoinCreditCard, [number, name], (err, results) => {
            if (err) {
              console.error(err);
            } else {
              console.log(results);
            }
          });
        }
      });

      res.status(201).send({ success: true, message: 'New credit card was created', data: {name, type, number, expiration, svss} });
    } catch (error) {
      res.status(500).send({ success: false, message: 'Server error' });
    }
    
    await next;
  }

  const createCreditOption = async (req, res, next) => {
    const {
      name,
      credit_amount,
      timeframe, 
      interest
    }: {
      name: String,
      credit_amount: String,
      timeframe: String,
      interest: String
    } = req.body;

    const createAt = new Date(Date.now());

    try {
      const createNewCreditOption = 'INSERT INTO create_credit_option (name, credit_amount, timeframe, interest) VALUES (?,?,?,?)';
      con.query(createNewCreditOption, [name, credit_amount, timeframe, interest], (err, results) => {
        if (err) {
          console.error(err);
        } else {
          console.log(results);
          };
      });

      res.status(201).send({ success: true, message: 'Credit was created', data: {name, credit_amount, timeframe, interest} });
    } catch (error) {
      res.status(500).send({ success: false, message: 'Server error' });
    }
    
    await next;
  }

  function listAllCreditGivers() {
    return new Promise((resolve, reject) => {
      const listCreditGivers = 'SELECT * FROM create_credit_option'; 
      con.query(listCreditGivers, (err, results) => {
        if (err) {
          console.error(err);
        } else {
          console.log(results);
      };
    });
  })};
  
  const listOfAllCreditGivers = async (req, res, next) => {
    try {
      const CreditGivers: Array = await listAllCreditGivers();
      res.status(200).send({ success: true, message: 'A list of all Credit Givers:', body: CreditGivers });
    } catch (error) {
        res.status(500).send({ success: false, message: 'internal server error'});
      }
    await next;
  }


  function searchCreditByName() {
    return new Promise((resolve, reject) => {
      const searchByName = 'SELECT * FROM create_credit_option';
      con.query(searchByName, (err, results) => {
        if (err) {
          console.error(err);
        } else {
          console.log(results);
      };
    });
  })};

  const AllCreditsByName = async (req, res, next) => {
      const { name } : { name : string } = req.params;
      try {
        const CreditsByName: Array = await searchCreditByName(name);
        res.status(200).send({ success: true, message: `Credits by the name ${name} are:`, body: CreditsByName });
      } catch (error) {
        res.status(500).send({ success: false, message: `internal server error`});
      }
      await next;
  }

  
  const searchCreditByAmount = async (req, res, next) => {
    return new Promise((resolve, reject) => {
      const searchByAmount = 'SELECT * FROM create_credit_option';
      con.query(searchByAmount, (err, results) => {
        if (err) {
          console.error(err);
        } else {
          console.log(results);
      };
    });
  })};

  const AllCreditsByAmount = async (req, res, next) => {
    const { credit_amount } : { credit_amount : string } = req.params;
    try {
      const CreditByAmount: Array = await searchCreditByAmount(credit_amount);
      res.status(200).send({ success: true, message: `Credits by the amount of ${credit_amount} are:`, body: CreditByAmount });
    } catch (error) {
      res.status(500).send({ success: false, message: `internal server error`});
    }
    await next;
}


  const Transaction = async (req, res, next) => {
    const {
      sendTo,
      amount
    }: {
      sendTo: String,
      amount: String,
    } = req.body;

    const createAt = new Date(Date.now());

    try {
      const MakeATranscation = 'INSERT INTO transactions_made (sendTo, amount) VALUES (?,?)';
      con.query(MakeATranscation, [sendTo, amount], (err, results) => {
        if (err) {
          console.error(err);
        } else {
          console.log(results);
          };
      });

      res.status(201).send({ success: true, message: 'The transaction was successful', data: {sendTo, amount} });
    } catch (error) {
      res.status(500).send({ success: false, message: 'Server error' });
    }
    
    await next;
  }




  export default {
      createUser,
      login,
      createCreditCard,
      createCreditOption,
      listOfAllCreditGivers,
      Transaction,
      ShowUserProfile,
      UpdateUserDetails,
      deleteUser,
      AllCreditsByName,
      AllCreditsByAmount
  }

