import { Router } from 'express';
import actions from './actions';

const { 
    createUser, 
    login, 
    createCreditCard, 
    createCreditOption,
    listOfAllCreditGivers,
    Transaction,
    ShowUserProfile,
    UpdateUserDetails,
    deleteUser,
    AllCreditsByName,
    AllCreditsByAmount
  } = actions;
  
  const mainRouter = Router();
  
  mainRouter.post('/sign-up', createUser);
  mainRouter.post('/login', login);
  mainRouter.get('/users/:id', ShowUserProfile);
  mainRouter.put('/users/:id', UpdateUserDetails);
  mainRouter.delete('users/:id', deleteUser);
  mainRouter.post('/create-creditcard', createCreditCard);
  mainRouter.post('/create-credit', createCreditOption);
  mainRouter.get('/credit-givers', listOfAllCreditGivers);
  mainRouter.get('/credit-givers/:name', AllCreditsByName);
  mainRouter.get('/credit-givers/:amount', AllCreditsByAmount);
  mainRouter.post('/transaction', Transaction);



  export default mainRouter;

