con.connect(() => {
  console.log('db connection is on');
  con.query(userCreateModel);
  con.query(CreditCardModel);
  con.query(CreateCreditOptionModel);
  con.query(TransactionModel);
  con.query(CreateDonationModel);
  con.query(DonateModel);

  
});

export default { con };

